import React, { useState, useRef, useEffect } from 'react';
import { Upload, MessageSquare, Search } from 'lucide-react';
import './App.css';

// Main application component
function App() {
  // State management
  const [file, setFile] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [ready, setReady] = useState(false);
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);
  const inputRef = useRef(null);

  // Document processing function
  const processDocument = async (uploadedFile) => {
    if (!uploadedFile) return;

    setProcessing(true);

    try {
      // In a full implementation, we would send the file to the backend
      // For now, we'll simulate processing with a delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      setReady(true);
      setMessages([{
        role: 'system',
        content: `Document "${uploadedFile.name}" has been processed. You can now ask questions about its content.`
      }]);
    } catch (error) {
      setMessages([{
        role: 'system',
        content: `Error processing document: ${error.message}`
      }]);
    } finally {
      setProcessing(false);
    }
  };

  // Submit question handler
  const handleSubmitQuestion = async () => {
    if (!question.trim() || !ready) return;

    // Add user question to chat
    const userQuestion = question;
    setMessages(prev => [...prev, {
      role: 'user',
      content: userQuestion
    }]);

    setQuestion('');
    setIsTyping(true);

    try {
      // In a full implementation, we would send the question to the backend
      // For now, we'll simulate with a delay
      await new Promise(resolve => setTimeout(resolve, 2000));

      const simulatedResponse = getSimulatedResponse(userQuestion);

      setMessages(prev => [...prev, {
        role: 'assistant',
        content: simulatedResponse
      }]);
    } catch (error) {
      setMessages(prev => [...prev, {
        role: 'system',
        content: `Error: ${error.message}`
      }]);
    } finally {
      setIsTyping(false);
    }
  };

  // Simulated response based on question patterns
  const getSimulatedResponse = (question) => {
    const lowerQuestion = question.toLowerCase();

    if (lowerQuestion.includes('architecture') || lowerQuestion.includes('how does this work')) {
      return "This system uses a Retrieval-Augmented Generation (RAG) architecture. It processes documents by splitting them into chunks, generating embeddings for semantic search, and then retrieves relevant context when answering questions. The LLM combines this retrieved context with the conversation history to generate accurate, contextually relevant responses.";
    } 
    else if (lowerQuestion.includes('author') || lowerQuestion.includes('who wrote')) {
      return "Based on the document analysis, the author appears to be Sarah Johnson, a data scientist with 8 years of experience in natural language processing and machine learning applications.";
    }
    else if (lowerQuestion.includes('summary') || lowerQuestion.includes('main points')) {
      return "The document discusses advances in large language models, focusing on three key areas: 1) Improvements in context window size, 2) Multimodal capabilities integrating text with images and audio, and 3) Fine-tuning techniques for specialized applications. It concludes with predictions about future developments in AI assistants and their impact on knowledge work.";
    }
    else if (lowerQuestion.includes('conclusion') || lowerQuestion.includes('findings')) {
      return "The document concludes that while LLMs have made remarkable progress, challenges remain in areas of factuality, reasoning, and alignment with human values. It suggests that hybrid systems combining neural approaches with symbolic reasoning may offer the most promising path forward for the next generation of AI systems.";
    }
    else {
      return "Based on the document content, I can tell you that this question relates to advanced language models and their applications. The document specifically mentions that these technologies are transforming how we interact with information, creating more intuitive and context-aware systems. Would you like me to elaborate on any specific aspect mentioned in the document?";
    }
  };

  // Auto-scroll to bottom of chat
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Handle file selection
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const selectedFile = e.target.files[0];
      setFile(selectedFile);
      processDocument(selectedFile);
    }
  };

  // Handle key press for question input
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSubmitQuestion();
    }
  };

  return (
    <div className="app-container">
      {/* Header */}
      <header className="app-header">
        <h1>Document Q&A System</h1>
        <p>Powered by AI for intelligent document analysis</p>
      </header>

      {/* Main content */}
      <main className="app-main">
        {/* Document upload area */}
        {!file && !processing && (
          <div className="upload-area">
            <Upload size={48} className="upload-icon" />
            <h2>Upload a Document</h2>
            <p>
              Upload a text document (PDF, DOCX, TXT) to analyze and ask questions about
            </p>
            <label className="upload-button">
              Select Document
              <input 
                type="file" 
                accept=".pdf,.docx,.txt" 
                className="hidden-input" 
                onChange={handleFileChange}
              />
            </label>
          </div>
        )}

        {/* Processing indicator */}
        {processing && (
          <div className="processing-indicator">
            <div className="spinner"></div>
            <p>Processing document...</p>
            <p className="processing-detail">Analyzing content and generating embeddings</p>
          </div>
        )}

        {/* Chat interface */}
        {(ready || messages.length > 0) && (
          <div className="chat-container">
            <div className="chat-messages">
              {messages.map((msg, i) => (
                <div 
                  key={i} 
                  className={`chat-message ${msg.role}`}
                >
                  {msg.content}
                </div>
              ))}
              {isTyping && (
                <div className="chat-message assistant typing">
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              )}
              <div ref={chatEndRef} />
            </div>

            {/* Question input */}
            <div className="question-input">
              <input
                ref={inputRef}
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask a question about the document..."
                disabled={!ready}
              />
              <button 
                onClick={handleSubmitQuestion}
                className={!ready || !question.trim() ? 'disabled' : ''}
                disabled={!ready || !question.trim()}
              >
                <Search size={20} />
              </button>
            </div>
          </div>
        )}
      </main>

      {/* Footer with system info */}
      <footer className="app-footer">
        <div className="footer-content">
          <div>
            <strong>System:</strong> Document Q&A with RAG Architecture
          </div>
          {file && (
            <div>
              <strong>Document:</strong> {file.name}
            </div>
          )}
        </div>
      </footer>
    </div>
  );
}

export default App;
